import React, { Component } from 'react';
import { withRouter } from 'react-router';
import { connect } from 'react-redux';
import { fetchProject } from "../../actions/projectsActions";

class Project extends Component {

    componentDidMount() {
        this.props.fetchProject(this.props.match.params.id);
    }

    renderCarousel() {
        return (
            <div class="gallery js-flickity" 
                data-flickity-options='{ "freeScroll": true, "autoPlay": true, "wrapAround": true }'>
                <div class="gallery-cell"></div>
                <div class="gallery-cell"></div>
                <div class="gallery-cell"></div>
            </div>
        );
    }

    render() {
        const {id, name, description, images, githubURL} = this.props.project;

        return (
            <div className="project__container">
                <div className="project__carousel">
                    {this.renderCarousel()}
                </div>
                <div className="project__card__text">
                    <h1 className="project__card__title">{name}</h1>
                    <p className="projects__card__description">{description}</p>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        ...state,
        project: state.project
    }
} 


export default withRouter(connect(mapStateToProps, { fetchProject })(Project));
