                <h1 className="courses__card__title">{title}</h1>
                <p className="courses__card__description">{description}</p>
                <article className="courses__card__iconContainer">
                    <a href={githubURL} target="_blank" rel="noreferrer"><FontAwesomeIcon icon={faGithub} className="courses__card__icon" /></a>
                    {URL ? linkIcon : "" }
                </article>
            </section>
            <section className="courses__card__img">
                <img src={imgURL} alt={title} />
            </section>
        </article>
    );
};

export default CourseCard;
